// Declare volume as a global variable
static uint8_t volume = 10;

void pwm_event_handler(nrfx_pwm_evt_type_t event_type);

void pwm_init(uint32_t output_pin) {
    nrfx_pwm_config_t config0 = {
        .output_pins = {
            output_pin | NRFX_PWM_PIN_INVERTED, // channel 0
            NRFX_PWM_PIN_NOT_USED, // channel 1
            NRFX_PWM_PIN_NOT_USED, // channel 2
            NRFX_PWM_PIN_NOT_USED  // channel 3
        },
        .base_clock = NRF_PWM_CLK_2MHz,
        .count_mode = NRF_PWM_MODE_UP,
        .top_value = 250,
        .load_mode = NRF_PWM_LOAD_COMMON,
        .step_mode = NRF_PWM_STEP_AUTO
    };

    nrfx_pwm_init(&pwm0, &config0, pwm_event_handler);
}

void pwm_event_handler(nrfx_pwm_evt_type_t event_type) {
    if (event_type == NRFX_PWM_EVT_FINISHED) {
        if (buffer_needs_update) {
            for (size_t i = 0; i < BUFFER_SIZE; ++i) {
                if (wavetable_index >= wavetable_length) wavetable_index = 0; // Wrap around the wavetable
                uint32_t scaled_value = (wavetable[wavetable_index++] * NRF_PWM0->COUNTERTOP) / 255;
                pwm_buffer[i] = (scaled_value * volume) / 100; // Apply volume scaling
            }
            buffer_needs_update = false; // Reset the flag
        }
    }
}

